mapper.select:
select
    *
from
    ac_contest_question

    contest_id = #{contestId}


       and  create_by_name = #{name}


        and question_id = #{questionId}

    and del_flag = #{delFlag}


limit
    #{startnum},#{endnum}

select
    count(*)
from
ac_contest_question

    contest_id = #{contestId}


        and  create_by_name = #{name}


        and question_id = #{questionId}

    and del_flag = #{delFlag}


limit
#{startnum},#{endnum}
