mapper.select:
select id, title, introduce, technology_type, create_time, ( select name from user where create_by = id ) as 'create_by', likes, status from article
del_flag = 1
status = #{status} and del_flag = #{delFlag}
ORDER BY create_time desc limit #{start},#{size}

mapper.update:
update
    article
set
    status =  #{status}
where
    id = #{id}
